      function loadFile(element, filter) {
        if (element.files[0].size / 1024 > 3000) {
          element.value = '';
          $(element).closest('label').find('.file_info').css({'color': 'red'});
          $(element).closest('label').find('.file_info').text('Слишком большой файл!');
        }
        else {
          if (filter) {
            if (element.files[0].type == 'application/pdf' || element.files[0].type == 'image/png' || element.files[0].type == 'image/jpeg') {
              $(element).closest('label').find('.file_info').removeAttr('style');
              $(element).closest('label').find('.file_info').text(element.files[0].name + ' (' + (element.files[0].size / 1024).toFixed(2) + 'Кб)');
            }
            else {
              element.value = '';
              $(element).closest('label').find('.file_info').css({'color': 'red'});
              $(element).closest('label').find('.file_info').text('Файл не является изображением или pdf документом!');
            }
          }
          else {
            $(element).closest('label').find('.file_info').removeAttr('style');
            $(element).closest('label').find('.file_info').text(element.files[0].name + ' (' + (element.files[0].size / 1024).toFixed(2) + 'Кб)');
          }
        }
      }
