    <script>
      function validateEmail (email) {
        return email.match(
          /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        );
      }

      function validatePhone(phone) {
        if (!phone) {
          return false;
        }

        return phone.match(
          /^((8|\+7)[\- ]?)?(\(?\d{3,5}\)?[\- ]?)?[\d\- ]{5,10}$/
        );
        // return true
      }

      function validateName(name) {
        return name.match(
          /[^\u0400-\u04FF A-Za-z]/
        );
      }

      jQuery('input[name=f_Email]').each(function(element) {
        jQuery(this).change(function() {
          if (jQuery(this).val() && !validateEmail(jQuery(this).val())) {
            jQuery(this).val('')
            jQuery(this).css( { 'border-color': 'red' } );
          }
          else {
            jQuery(this).removeAttr('style');
          }
        })
      })

      jQuery('input[name=f_Phone]').each(function(element) {
        jQuery(this).change(function() {
          if (jQuery(this).val() && !validatePhone(jQuery(this).val())) {
            jQuery(this).val('')
            jQuery(this).css( { 'border-color': 'red' } );
